import React, { useEffect, useState } from 'react';
import Chapter from './chapter';
import ReactDOM from 'react-dom';
import '../../Styles/course.css'

const CourseTemplate = ({ course, chapters}) => {


    const handleClick = (id) =>{
    	console.log(id);
      const container = document.querySelector('div.chapter-view');

        //ReactDOM.unmountComponentAtNode(container);
        //ReactDOM.render(<Chapter />, container);
        container.innerHTML=<Chapter />

    }

    return (
        <div className="course-template">
           <div className="course_head">
               <h1> {course.title} </h1>
               {/*<p>
                                  <i className="fas fa-user"></i> {course.author} 
                                  <i className="fas fa-clock"></i><small> {course.createdAt}  </small>  
                              </p>*/}
           </div>
          <div className="course-body">
           <div className="chapters">
            { chapters.map(chapter=>(
            		<div key={chapter.id} onClick={()=>handleClick(chapter.id)} 
                className="chapter-detail text-center" >
            			<h5> {chapter.title} </h5>
            		</div>
            	)) 
            }
           </div>

           <div className="chapter-view">
               <div className="text-center"> <img src="http://placehold.it/300" alt={course.title} /></div>
               <div> { course.introduction } 

               </div>
               <div> { course.objectifs }
             REST framework is a collaboratively funded project. If you use REST framework commercially we strongly encourage you to invest in its continued development by signing up for a paid plan.

Every single sign-up helps us make REST framework long-term financially sustainable.
               ! </div>
           </div>
           </div>
        </div>
    );
};

export default CourseTemplate;
