import React, { useState, useEffect } from 'react';
import { fetchPosts } from '../service/blogAPI';
import PostItem from '../Components/post_item';
import SpinningDots from '@grafikart/spinning-dots-element'


const Blog = () =>{
	const [posts, setPosts] = useState([]);
	const [loading, setLoading] = useState(false);
	useEffect(()=>{

customElements.define('spinning-dots', SpinningDots)
        fetchPosts().then(r=>{
        	setPosts(r);
        	setLoading(true)
        }).catch(e=>console.log(e))
	}, []);

	return(
        <div>
         <section>
          { !loading && (  <spinning-dots style={{"width":200+"px", "strokeWidth":20+"px", "color": "#535FF6"}} dots="8">
   </spinning-dots>) }
        </section>
        <section>
          {/* loading && (<div> {posts.map(post=>(<PostItem post={post} />))} </div>)  */}
          { loading && (<div> {['posts', 'dd'].map(post=>console.log(post))} </div>) }
        </section>
        </div>
	);
}
export default Blog;
