import React, {useState } from 'react';
import Chapter from './chapter';
import ReactDOM from 'react-dom';
import '../../Styles/course.css'
import Media from './media'
const Component = ({course}) =>{

  return(
         <div> 
            <h4> {course.title} </h4>
            <small> {course.createdAt} </small>
            <strong> {course.author} </strong>
            <Media isVideo={course.is_video}
                title={course.slug} 
                media={course.media}
            />
            <div> {course.description} </div>
            <div> {course.objectifs} </div>

        </div>
    )
}

const CourseTemplate = ({ course, chapters}) => {

    const [div, setDiv] = useState(0);

    const handleClick = (id) =>{

        const container = document.querySelector('div.chapter-view');
        const mydiv = document.querySelector('div#chap'+id);
        mydiv.classList.add('currentChap');


          const mylastdiv = document.querySelector('div#chap'+div);
        if (mylastdiv) {
           mylastdiv.classList.remove('currentChap');
        } 

        setDiv(id);

        ReactDOM.unmountComponentAtNode(container);
        ReactDOM.render(<Chapter  id={id}/>, container);


    }

    return (
        <div className="course-template">
           <div className="course_head">
               <h1> {course.title} </h1>
               <i className="fas fa-clock"></i><small> {course.createdAt}  </small>  
           </div>
          <div className="course-body">
           <div className="chapters">
            { chapters.map(chapter=>(
            		<div key={chapter.id} 
                   onClick={()=>handleClick(chapter.id)} 
                   className="chapter-detail text-center" 
                   id={"chap"+chapter.id}
                   >

            			chap{chapter.id}:<h6> {chapter.title} </h6>

            		</div>
            	)) 
            }
           </div>

           <div className="chapter-view">
              <Component course={course} />
           </div>
           </div>
        </div>
    );
};

export default CourseTemplate;
