import React, { useState, useEffect } from 'react';
import {fetchCourses } from '../service/data';
//import CourseItem from '../Components/course_item';
import { Link  } from 'react-router-dom';
import Media from '../Components/course/media';
import { Helmet } from 'react-helmet';

const Courses = () => {
	const [courses, setCourses] = useState([]);
	const [loading, setLoading] = useState(false);
	useEffect(()=>{
		fetchCourses().then(response=>{
			setCourses(response)
			setLoading(true)
		}).catch(e=>console.log(e))
	}, []);
    return (
        <div>
            {!loading && (<h1>loading ....</h1>)}
         <Helmet>
               <title>najmi-imad.com| Courses</title>
            </Helmet>
           {courses.map(course=>(
            <div className="course">

              <Link to={"/courses-"+course.slug}>
              <h1> {course.title} </h1>
                  <Media isVideo={course.is_video} 
                     title={course.title}  
                     media={course.media} 
                  />
              </Link>
              <div>
                  {course.objectifs}
                     <p> lorem ipsum dolor sit</p>
                     <p> lorem ipsum dolor sit</p>
                     <p> lorem ipsum dolor sit</p>
              </div>


            </div>
            ))}
        </div>
    );
};


export default Courses;
