import React, { useEffect, useState } from 'react';
import { fecthChapter } from '../../service/data';
import Media from './media';

const Chapter = ({ id }) => {
    const [chapter, setChapter] = useState({});

	useEffect(()=>{
		fecthChapter(id)
		.then(res=>setChapter(res))
		.catch(err=>{
			console.log(err);
		})
	}, [id]);

    return (
        <>
          <div className="chapter-title">
          	<h4> { chapter.title } </h4>
          	<small>  <i className="fas fa-clock"></i> {chapter.createdAt} </small>
          	<strong> <i className="fas fa-user"></i> "Imad Najmi" </strong>
            </div>

             <Media isVideo={chapter.is_video}
              title={chapter.title}  media={chapter.media} 
               width={400}
                height={300}
              />

          <div className="chapter-boody">
          	{chapter.body}
          </div>
        </>
    );
};


export default Chapter;
