import React, { useEffect, useState } from 'react';
import { fecthChapter } from '../../service/data';
const Chapter = ({ id }) => {
    const [chapter, setChapter] = useState({});

	useEffect(()=>{
		fecthChapter(id)
		.then(res=>setChapter(res))
		.catch(err=>{
			console.log(err);
		})
	}, [id]);

    return (
        <>
          <div className="chapter-title">
          	{ chapter.title }
          	<small>  <i className="fas fa-clock"></i> {chapter.createdAT} </small>
          	<strong> <i className="fas fa-user"></i> {chapter.author} </strong>
          </div>
          <div className="chapter-media">
          	 <img src={chapter.media} alt={chapter.title} />
          </div>
          <div className="chapter-boody">
          	{chapter.body}
          </div>
        </>
    );
};


export default Chapter;
